import { type NextRequest } from 'next/server';
import { createI18nMiddleware } from 'next-international/middleware';
import { i18n } from './locales/config';
import { createServerSupabaseClientForMiddleware } from '@/lib/supabase-server';

const I18nMiddleware = createI18nMiddleware({
  locales: i18n.locales,
  defaultLocale: i18n.defaultLocale,
});

export async function middleware(request: NextRequest) {
  const response = I18nMiddleware(request);
  const supabase = createServerSupabaseClientForMiddleware(request, response);

  await supabase.auth.getSession();

  return response;
}

export const config = {
  matcher: [
        '/((?!api|_next/static|_next/image|assets|flags|illustrations|favicon.ico|auth/callback|.*\\..*).*)',
  ],
};

import { createServerClient, type CookieOptions } from '@supabase/ssr';
import type { NextRequest, NextResponse } from 'next/server';
import type { SupabaseClient } from '@supabase/supabase-js';
import type { Database } from '@/types/database';

type TypedSupabaseClient = SupabaseClient<Database>;

export function createServerSupabaseClient(cookieStore: ReturnType<typeof import('next/headers').cookies>): TypedSupabaseClient {
  return createServerClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        get(name: string) {
          return cookieStore.get(name)?.value;
        },
        set(name: string, value: string, options: CookieOptions) {
          cookieStore.set({ name, value, ...options });
        },
        remove(name: string, options: CookieOptions) {
          cookieStore.set({ name, value: '', ...options });
        },
      },
    }
  );
}

export function createServerSupabaseClientForMiddleware(
  request: NextRequest,
  response: NextResponse
): TypedSupabaseClient {
  return createServerClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        get(name: string) {
          return request.cookies.get(name)?.value;
        },
        set(name: string, value: string, options: CookieOptions) {
          response.cookies.set({
            name,
            value,
            ...options,
          });
        },
        remove(name: string, options: CookieOptions) {
          response.cookies.set({
            name,
            value: '',
            ...options,
          });
        },
      },
    }
  );
}

export type { TypedSupabaseClient };
